  <script>
    // 알 상태 자동 업데이트
    function eggUpdate() {
      let currentEggCount = localStorage.getItem('eggCount');
      if (currentEggCount === null) {
        currentEggCount = 0;
      } else {
        // 문자열로 저장되어 있으므로 숫자로 변환
        currentEggCount = parseInt(currentEggCount, 10);
      }

      // 증가된 숫자를 화면에 반영
      document.querySelector('.header-egg span').innerText = currentEggCount;

      // 증가된 숫자를 localStorage에 저장
      localStorage.setItem('eggCount', currentEggCount);
    }

    function gopage(link) {
      if (link == 'quiz') {
        window.location.href = "/es/reward/";
      } else if (link == 'form') {
        window.location.href = "/es/reward/sign";
      } else if (link == 'mypage') {
        window.location.href = "/es/reward/mypage";
      } else if (link == 'list') {
        window.location.href = "/es/reward/list";
      } else if (link == 'result') {
        window.location.href = "/es/reward/result";
      } else if (link == 'home') {
        window.location.href = "https://www.banggooso.com/";
      };
    }
  </script>